configuration.scripts:

				window.onload = function() {
				var link = document.createElement('link');
				link.setAttribute('rel', 'stylesheet');
				link.setAttribute('type', 'text/css');
				link.setAttribute('href', '../../resources/extentReports/extent.css');
                document.head.appendChild(link);

				var jqueryfile = document.createElement('script');
				jqueryfile.setAttribute('src', '../../resources/extentReports/jquery.js');
				jqueryfile.setAttribute('type', 'text/javascript');
                document.body.appendChild(jqueryfile);

				var icons = document.createElement('link');
				icons.setAttribute('rel', 'stylesheet');
				icons.setAttribute('type', 'text/css');
				icons.setAttribute('href', '../../resources/extentReports/materialicons.css');
                document.head.appendChild(icons);

				var styles = document.createElement('link');
				styles.setAttribute('rel', 'stylesheet');
				styles.setAttribute('type', 'text/css');
				styles.setAttribute('href', '../../resources/extentReports/styles.css');
                document.head.appendChild(styles);
            };
